'use strict';

//常に正の数の答えを返す剰余演算をする関数 (負の数の剰余演算を処理するため)
function mod(n, m) {
    return ((n % m) + m) % m;
};

// 四捨五入して小数点第3位までを表示する関数 (JavaScriptには元からそういう関数が無いっぽいので)
function roundToThree(num) {
    return +(Math.round(num + "e+3") + "e-3");
};

//2つの数を比較して大きい方を返す関数
function aryMax(a, b) {
    return Math.max(a, b);
};

//2つの数を比較して小さい方を返す関数
function aryMin(a, b) {
    return Math.min(a, b);
};

//変数contextを定義する
let context;

//AudioContextを作成する関数
function init() {
    try {
        //webkitプレフィックスをつける。（WebKit使用のブラウザに対応するため）
        window.AudioContext
            = window.AudioContext || window.webkitAudioContext;
        //AudioContextを生成する
        context = new AudioContext();
        console.log('new AudioContext');
    } catch (e) {
        //try内の処理がエラーの場合、それをユーザーに伝える。
        alert('このブラウザではWeb Audio APIはサポートされていません。（音が出せません。）Web Audio API is not supported by this browser. (Cannot play sound.)');
    };
    return context;
};

// MIDIノートナンバーを周波数に変換する関数
function ConvertMIDItoHZ(MIDI_note_number) {
    let freq = 2 ** ((MIDI_note_number - 69) / 12) * 440;
    return freq;
};

